mapper.select:
SELECT a.`PK_ASSET`,d.`PROV_NAME` AS`PROV_CODE`,a.`ASSET_NAME`,a.`ASSET_IP`,b.`TYPE_NAME`,c.`BS_NAME` ,
(CASE a.`COME_FROM` WHEN 1 THEN '人工录入' WHEN 2 THEN 'Excel导入' WHEN 3 THEN '4A同步' WHEN 4 THEN '未知资产接入' END) AS COME_FROM,
(CASE a.`ASSET_STATE` WHEN 1 THEN '临时' WHEN 2 THEN '有效' WHEN 3 THEN '停用' WHEN 4 THEN '删' END ) AS ASSET_STATE
FROM `am_asset` a,`am_asset_type` b,`am_bs_prov` c,`bdm_prov` d
WHERE a.`PK_ASSET_TYPE`=b.`PK_ASSET_TYPE`
AND a.`PROV_CODE`=d.`PROV_CODE`
AND a.`PK_PROV_BS`=c.`PK_BS_PROV`


          #{a.`PK_ASSET`}



  AND a.`PROV_CODE`=#{AmAssetQuery.provCode}


  AND a.`ASSET_NAME` LIKE #{AmAssetQuery.assetName}


  AND a.`ASSET_IP` LIKE #{AmAssetQuery.assetIp}


  AND b.`TYPE_NAME`=#{AmAssetQuery.typeName}


  AND c.`BS_NAME`=#{AmAssetQuery.bsName}


  AND a.`ASSET_STATE`=#{AmAssetQuery.assetState}


  AND a.`COME_FROM`=#{AmAssetQuery.comeFrom}
