mapper.select:
    SELECT a.*,b.COLLECT_TYPE as COLLECT_TYPE2,b.COME_FROM as COME_FROM2 ,
     b.IS_VIEW as IS_VIEW2,b.NEED_VALUE as NEED_VALUE2
    FROM `am_asset_prop` a ,`am_asset_type_prop` b
WHERE a.`PK_ASSET_PROP` = b.`PK_ASSET_PROP`
AND b.`PK_ASSET_TYPE` =#{id}
and a.PROP_NAME like #{name};

SELECT a.`PK_ASSET_PROP`, a.`PROP_NAME`,a.`DATA_TYPE`,b.`VALUE_NAME` FROM (SELECT a.`PK_ASSET_PROP`, a.`PROP_NAME`,a.`DATA_TYPE` FROM `am_asset_prop` a
WHERE a.`PK_ASSET_PROP` IN (SELECT a.`PK_ASSET_PROP` FROM `am_asset_type_prop` a
WHERE a.`PK_ASSET_TYPE`=#{id}
)) a LEFT JOIN `am_asset_prop_value` b
ON a.PK_ASSET_PROP=b.`PK_ASSET_PROP`
